mapper.insert:
  		insert into Customer (
  			userBn
  			, customerNo
  			, customerDiv
  			, customerBn
  			, customerCname
  			, customerName
  			, customerCellNo
  			, customerOfficeNo
  			, customerFaxNo
  			, customerEmail
  			, customerZipcode
  			, customerAddress
  			, rankDiv
  			, inCharge
  			, customerNote
  		) values (
			#{userBn}
			, customerNo_seq.nextval
			, #{customerDiv}
			, #{customerBn}
			, #{customerCname}
			, #{customerName}
			, #{customerCellNo}
			, #{customerOfficeNo}
			, #{customerFaxNo}
			, #{customerEmail}
			, #{customerZipcode}
			, #{customerAddress}
			, #{rankDiv}
			, #{inCharge}
			, #{customerNote}
  		)

mapper.update:
update Customer
set
	customerDiv = (SELECT customerDiv FROM tb_customerDiv WHERE customerExp = '${customerDiv}')
	, customerBn = #{customerBn}
	, customerCname = #{customerCname}
	, customerName = #{customerName}
	, customerCellNo = #{customerCellNo}
	, customerFaxNo = #{customerFaxNo}
	, customerEmail = #{customerEmail}
	, customerZipcode = #{customerZipcode}
	, customerAddress = #{customerAddress}
	, rankDiv = (SELECT rankDiv FROM tb_rankDiv WHERE rankExp = '${rankDiv}')
	, customerNote = #{customerNote}
where customerNo = #{customerNo}
